import React, { useState, useEffect } from 'react';
import { Container, Typography, AppBar, Toolbar, Box, CssBaseline } from '@mui/material';
import IngredientInput from './components/IngredientInput';
import RecipeList from './components/RecipeList';
import './App.css';

const API_ID = process.env.REACT_APP_EDAMAM_APP_ID;
const API_KEY = process.env.REACT_APP_EDAMAM_APP_KEY;

function App() {
  const [ingredients, setIngredients] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const addIngredient = (ingredient) => {
    if (!ingredients.includes(ingredient.toLowerCase())) {
      setIngredients([...ingredients, ingredient.toLowerCase()]);
    }
  };

  const removeIngredient = (ingredientToRemove) => {
    setIngredients(ingredients.filter(ing => ing !== ingredientToRemove));
  };

  useEffect(() => {
    const fetchRecipes = async () => {
      if (ingredients.length === 0) {
        setRecipes([]);
        return;
      }

      setIsLoading(true);
      try {
        // Create a comma-separated string of ingredients
        const query = ingredients.join(',');
        console.log('Fetching recipes for ingredients:', query);

        // Make the API request
        const response = await fetch(
          `https://api.edamam.com/api/recipes/v2?type=public&q=${encodeURIComponent(query)}&app_id=${API_ID}&app_key=${API_KEY}&from=0&to=12`
        );

        if (!response.ok) {
          throw new Error(`API request failed with status ${response.status}`);
        }

        const data = await response.json();
        console.log('API Response:', data);

        if (data?.hits?.length > 0) {
          setRecipes(data.hits.map(hit => ({
            ...hit.recipe,
            // Ensure we have all required fields with fallbacks
            label: hit.recipe.label || 'Untitled Recipe',
            image: hit.recipe.image || 'https://via.placeholder.com/300x200?text=No+Image',
            ingredientLines: hit.recipe.ingredientLines || [],
            calories: hit.recipe.calories || 0,
            totalTime: hit.recipe.totalTime || 30, // Default to 30 minutes if time not specified
            url: hit.recipe.url || '#',
            source: hit.recipe.source || 'Unknown Source'
          })));
        } else {
          setRecipes([]);
        }
      } catch (error) {
        console.error('Error fetching recipes:', error);
        // Set a sample recipe if there's an error (for testing)
        setRecipes([{
          label: 'Sample Recipe (API Error)',
          image: 'https://via.placeholder.com/300x200?text=API+Error',
          ingredientLines: ['Check your API keys', 'Make sure internet connection is working', 'Verify Edamam API credentials'],
          calories: 0,
          totalTime: 0,
          url: '#',
          source: 'System'
        }]);
      } finally {
        setIsLoading(false);
      }
    };

    const timer = setTimeout(() => {
      fetchRecipes();
    }, 500);

    return () => clearTimeout(timer);
  }, [ingredients]);

  return (
    <>
      <CssBaseline />
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            🍳 Recipe Finder
          </Typography>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Box sx={{ mb: 4 }}>
          <Typography variant="h4" component="h1" gutterBottom>
            Find Recipes with Your Ingredients
          </Typography>
          <Typography variant="body1" color="textSecondary" paragraph>
            Add the ingredients you have at home to discover delicious recipes!
          </Typography>

          <IngredientInput onAddIngredient={addIngredient} />

          <Box sx={{ mt: 2, display: 'flex', flexWrap: 'wrap', gap: 1 }}>
            {ingredients.map((ingredient, index) => (
              <Box
                key={index}
                component="span"
                sx={{
                  bgcolor: 'primary.light',
                  color: 'white',
                  px: 1.5,
                  py: 0.5,
                  borderRadius: 1,
                  display: 'inline-flex',
                  alignItems: 'center',
                  cursor: 'pointer',
                  '&:hover': {
                    bgcolor: 'primary.dark',
                  },
                }}
                onClick={() => removeIngredient(ingredient)}
              >
                {ingredient}
                <span style={{ marginLeft: '8px', fontSize: '0.8rem' }}>×</span>
              </Box>
            ))}
          </Box>
        </Box>

        {isLoading ? (
          <Typography variant="h6" align="center">Loading recipes...</Typography>
        ) : (
          <RecipeList recipes={recipes} />
        )}
      </Container>
    </>
  );
}

export default App;
