import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  AppBar, 
  Toolbar, 
  Box, 
  CssBaseline, 
  CircularProgress,
  Chip
} from '@mui/material';
import RestaurantIcon from '@mui/icons-material/Restaurant';
import IngredientInput from './components/IngredientInput';
import RecipeList from './components/RecipeList';
import './App.css';

const API_ID = process.env.REACT_APP_EDAMAM_APP_ID;
const API_KEY = process.env.REACT_APP_EDAMAM_APP_KEY;

function App() {
  const [ingredients, setIngredients] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const addIngredient = (ingredient) => {
    const trimmedIngredient = ingredient.trim().toLowerCase();
    if (trimmedIngredient && !ingredients.includes(trimmedIngredient)) {
      setIngredients([...ingredients, trimmedIngredient]);
      setError(null);
    }
  };

  const removeIngredient = (ingredientToRemove) => {
    setIngredients(ingredients.filter(ing => ing !== ingredientToRemove));
    setError(null);
  };

  useEffect(() => {
    const fetchRecipes = async () => {
      if (ingredients.length === 0) {
        setRecipes([]);
        return;
      }

      setIsLoading(true);
      setError(null);

      try {
        // Verify API credentials
        if (!API_ID || !API_KEY || API_ID === 'your_app_id_here' || API_KEY === 'your_app_key_here') {
          throw new Error('API credentials not configured. Please update your .env file with valid Edamam API credentials.');
        }

        // Create a comma-separated string of ingredients
        const query = ingredients.join(',');
        console.log('Fetching recipes for ingredients:', query);

        // Make the API request
        const apiUrl = `https://api.edamam.com/api/recipes/v2?type=public&q=${encodeURIComponent(query)}&app_id=${API_ID}&app_key=${API_KEY}&from=0&to=12`;
        console.log('API URL:', apiUrl);

        const response = await fetch(apiUrl);

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          console.error('API Error:', errorData);
          throw new Error(`API request failed: ${response.status} ${response.statusText}`);
        }

        const data = await response.json();
        console.log('API Response:', data);

        if (data?.hits?.length > 0) {
          const formattedRecipes = data.hits.map(hit => ({
            ...hit.recipe,
            // Ensure we have all required fields with fallbacks
            label: hit.recipe.label || 'Untitled Recipe',
            image: hit.recipe.image || 'https://via.placeholder.com/300x200?text=No+Image',
            ingredientLines: hit.recipe.ingredientLines || [],
            calories: hit.recipe.calories || 0,
            totalTime: hit.recipe.totalTime || 30, // Default to 30 minutes if time not specified
            url: hit.recipe.url || '#',
            source: hit.recipe.source || 'Unknown Source',
            yield: hit.recipe.yield || 1
          }));
          setRecipes(formattedRecipes);
        } else {
          setRecipes([]);
          setError('No recipes found. Try different ingredients!');
        }
      } catch (error) {
        console.error('Error fetching recipes:', error);
        setError(`Error: ${error.message}. Please check your API credentials and internet connection.`);
        setRecipes([]);
      } finally {
        setIsLoading(false);
      }
    };

    const timer = setTimeout(() => {
      fetchRecipes();
    }, 500);

    return () => clearTimeout(timer);
  }, [ingredients]);

  return (
    <>
      <CssBaseline />
      <AppBar position="static">
        <Toolbar>
          <RestaurantIcon sx={{ mr: 2 }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1, fontWeight: 'bold' }}>
            Recipe Finder
          </Typography>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Box sx={{ mb: 4, textAlign: 'center' }}>
          <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main' }}>
            Find Recipes with Your Ingredients
          </Typography>
          <Typography variant="body1" color="text.secondary" paragraph>
            Add the ingredients you have at home to discover delicious recipes!
          </Typography>

          <Box sx={{ maxWidth: 600, mx: 'auto', mt: 3 }}>
            <IngredientInput onAddIngredient={addIngredient} />

            {ingredients.length > 0 && (
              <Box sx={{ mt: 2, display: 'flex', flexWrap: 'wrap', gap: 1, justifyContent: 'center' }}>
                {ingredients.map((ingredient, index) => (
                  <Chip
                    key={index}
                    label={ingredient}
                    onDelete={() => removeIngredient(ingredient)}
                    color="primary"
                    variant="outlined"
                    sx={{ 
                      '&:hover': { 
                        bgcolor: 'primary.light',
                        color: 'white',
                      } 
                    }}
                  />
                ))}
              </Box>
            )}
          </Box>
        </Box>

        {error && (
          <Box sx={{ 
            bgcolor: 'error.light', 
            color: 'white', 
            p: 2, 
            borderRadius: 1,
            mb: 3,
            textAlign: 'center'
          }}>
            <Typography>{error}</Typography>
          </Box>
        )}

        {isLoading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 8 }}>
            <CircularProgress />
          </Box>
        ) : recipes.length > 0 ? (
          <RecipeList recipes={recipes} />
        ) : ingredients.length > 0 ? (
          <Typography variant="h6" align="center" sx={{ my: 4, color: 'text.secondary' }}>
            No recipes found. Try different ingredients!
          </Typography>
        ) : (
          <Box sx={{ textAlign: 'center', my: 8 }}>
            <RestaurantIcon sx={{ fontSize: 64, color: 'action.disabled', mb: 2 }} />
            <Typography variant="h6" color="text.secondary">
              Add some ingredients to find recipes
            </Typography>
            <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
              Try: chicken, rice, tomatoes, etc.
            </Typography>
          </Box>
        )}
      </Container>
    </>
  );
}

export default App;
